def gcd(a,b):    if (b>a):        a,b=b,a    if b == 0:        return a    return gcd(b,a-b*(a//b))fin = open('B.in','r')fout = open('B.out','w')C=fin.next()i=1for line in fin:    nums = [int(n) for n in line.split(' ')[1:]]    m = nums[0]    T = abs(nums[0]-nums[1])    for num in nums[1:]:        T = gcd(T,abs(num-m))        if num < m:            m = num    y = (m//T) * T - m    if y<0:        y+=T    fout.write("Case #" + str(i) + ": " + str(y) + '\n')    i+=1