fin = open('B-large.in', 'rb')fout = open('output.txt', 'wb')T = int(fin.readline().strip())for case in range(1, T + 1):    count = 0    line = fin.readline().strip().split()    N = int(line[0])    S = int(line[1])    p = int(line[2])    for i in range(N):        t = sorted([int(x) for x in line[3:]])    for i in range(N):        x = t[i] % 3        y = t[i] / 3        if y >= p:            count += 1            #print t[i], (y, y, y), 'a'        elif S > 0 and x == 0 and y < 10 and y > 0 and y + 1 >= p:            count += 1            S -= 1            #print t[i], (y + 1, y, y - 1), 'b'        elif x == 1 and y < 10 and y + 1 >= p:            count += 1            #print t[i], (y + 1, y, y), 'c'        elif x == 2 and y < 10 and y + 1 >= p:            count += 1            #print t[i], (y + 1, y + 1, y), 'd'        elif S > 0 and x == 2 and y < 9 and y + 2 >= p:            count += 1            S -= 1            #print t[i], (y + 2, y, y), 'e'    #print    fout.write('Case #' + str(case) + ': ' + str(count) + '\n')fout.close()