import mathif __name__=='__main__':	f = open('A-small-attempt0.in')	games = f.readline()	out=open('out.txt', "w")  	for i in range(int(games)):		t=f.readline()		a=t.split(' ')		x=int(a[1])		r=int(a[0])		y=2*r-1		b='{:0.15f}'.format(math.sqrt(1.0*(y*y+8*x)))		res=(int(float(b))-y)/4		while res*res*2+y*res>x:			res-=1		print >> out, 'Case #'+str(i+1)+': '+str(res) 	f.close()