tc = int(input())for c in range(tc):    print('Case #', end="")    print(c+1,end="")    print(": ", end="")    n = int(input())    Nw = sorted(list(map(float, input().split())))    Kw = sorted(list(map(float, input().split())))    Nwc = list(Nw)    Kwc = list(Kw)    x = 0    while len(Nwc)>0:        w = Nwc[0]        if w > Kwc[0]:            Kwc.pop(0)            x = x +1        Nwc.pop(0)    print(x, end=" ")#----------------------------------------    y = 0    Nwc = list(Nw)    Kwc = list(Kw)    while n>0:        w = Nwc[n-1]        k = n-1        if Kwc[k] < w:            k = 0        else:            while k>0 and Kwc[k - 1]>w:                k = k - 1        if w > Kwc[k]:            y = y + 1        n = n - 1        Kwc.pop(k)    print(y)