def helper(n):    for x in range(n, -1, -1):        if tidy(x):            return xdef tidy(n):    s = str(n)    mini = 0    for x in range(len(s)):        if int(s[x]) < mini:            return False        else:            mini = int(s[x])    return Truet = int(input())for i in range(t):    n = int(input())    print ("Case #" + str(i+1) + ": " + str(helper(n)))